(function($){function applyOne(idx,element){element=$(element);var options=element.data("jtac-calculator");if(options){element.data("calculator",null);try{if(typeof options=="string"){options=window.eval("("+options+");")}element.calculator(options)}catch(e){jTAC.error("Could not parse the data-jtac-calculator attribute of id ["+element.get()[0].id+"] Error info:"+e.toString())}}}function apply(){try{jTAC._internal.pushContext("calculator-unobtrusive.apply()");var elements=$("input[type=hidden]");elements.each(applyOne)}finally{jTAC._internal.popContext()}}if(!window.jTAC_CalculatorOptions){jTAC.error("Requires the calculator scripts.")}$(document).ready(apply)})(jQuery);